import React, { Component } from 'react';
import './Kitap.css';
import KitapKapaklari2 from './KitapKapaklari2';
import KitapTanitimi2 from './Kitap/KitapTanitimi2';


class Kitap extends Component {
    constructor(props) {
		super(props);
		this.state = {
			liked: false,
		};
	}
  
  isLiked = () => {
		this.setState({liked: !this.state.liked});
	}

  render() {
   
    return (
      <div className='Kitap'>
        <KitapKapaklari2 img={this.props.img}></KitapKapaklari2>
    <KitapTanitimi2 name={this.props.name}></KitapTanitimi2>
    
      </div>
    );
  }
}
export default Kitap;
