import React, { Component } from 'react';
import axios from 'axios';

class KitapKayitFormu extends Component {
    constructor(props) {
        super(props);
        this.state = {    persons: []
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChange(event) {
        this.setState({
          [event.target.name] : event.target.value,
          
        });
        
      }
    
      handleSubmit(event) {
        event.preventDefault();
        let name = this.state.persons.name;
        let category=this.state.persons.category;
        let author=this.state.persons.author;
        let summary=this.state.persons.summary;
        
    
            axios.post('http://localhost:8080/books', {
              name: name,
              author: author,
              category:category,
              summary: summary
            
            }).then(message=>{
              this.setState({error : message.data })
            }).catch(err => {
              this.setState({error: err.data})
            });
            this.setState({name: '',
          category:'', author: '', summary: '',});       
        }
       
    
        
      
    render() {
        return (
            <div className = "Form">
            <form onSubmit={this.handleSubmit} >
            <div className= "custom"
                 style = {{border: this.state.ifNameExists}}>
            <label  >
                Kitabın Adı: 
                <input type="text"
                name = "name"
                value={this.state.name} 
                onChange={this.handleChange}
                required/>
              </label>
            </div>
              <div className= "custom" 
                   style = {{border: this.state.ifInvalid}}>
              <label  >
                Kategorisi: 
                <input type="text"              
                name = "category"
                value={this.state.category} 
                onChange={this.handleChange}
                required/>
              </label>
              </div>
              <div className= "custom" 
                   style = {{border: this.state.ifInvalid}}>
              <label  >
                Yazarı: 
                <input type="text"              
                name = "author"
                value={this.state.author} 
                onChange={this.handleChange}
                required/>
              </label>
              </div>
              <div className= "custom" 
                   style = {{border: this.state.ifInvalid}}>
              <label  >
                Kitaptan Bahsedin: 
                <input type="text"              
                name = "summary"
                value={this.state.summary} 
                onChange={this.handleChange}
                required/>
              </label>
              </div>
                           


              <div className= "custom2">
              <input className="Button" 
                     type="submit" 
                     value="KAYDET" />
              </div>
            </form>
              <div style={{color: this.state.ifAdded}}>{this.state.error}</div>
          </div>
        );
      }
      
    }
    

export default KitapKayitFormu;
