import React, { Component } from 'react';
import axios from 'axios';

class KitapKayitFormu extends Component {
    constructor(props) {
        super(props);
        this.state = {                  name:'',
                  
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChange(event) {
        this.setState({
          [event.target.name] : event.target.value
        });
        
      }
    
      handleSubmit(event) {
        event.preventDefault();
        let name = this.state.name;
        
    
            axios.post('http://localhost:8080/alerts', {
              name: name,
            
            }).then(message=>{
              this.setState({error : message.data })
            }).catch(err => {
              this.setState({error: err.data})
            });
            this.setState({name: '',});       
        }
       
    
        
      
    render() {
        return (
            <div className = "Form">
            <form onSubmit={this.handleSubmit} >
            <div className= "custom"
                 style = {{border: this.state.ifNameExists}}>
            <label  >
                Name: 
                <input type="text"
                name = "name"
                value={this.state.name} 
                onChange={this.handleChange}
                required/>
              </label>
            </div>
              <div className= "custom" 
                   style = {{border: this.state.ifInvalid}}>
              <label  >
                URL: 
                <input type="text"              
                name = "givenurl"
                value={this.state.givenurl} 
                onChange={this.handleChange}
                required/>
              </label>
              </div>
              <div className= "custom">
              <label>
              Request:  
              <select name="requestType" 
                      type=""
                      value={this.state.requestType} 
                      onChange={this.handleChange}  >            
                <option value = "POST">POST</option>
                <option value= "GET">GET</option>
                <option value= "DELETE">DELETE</option>
                <option value= "PUT">PUT</option>
              </select>
              </label>
              </div>
              <div className= "custom"
                   style= {{border: this.state.ifPeriodZero}}>
              <label >
                Period: 
                <input type="number"
                className ="Request"
                name = "period"
                value={this.state.period} 
                onChange={this.handleChange} 
                required/>
              </label>
              </div>
              <div className= "custom2">
              <input className="Button" 
                     type="submit" 
                     value="SUBMIT" />
              </div>
            </form>
              <div style={{color: this.state.ifAdded}}>{this.state.error}</div>
          </div>
        );
      }
      
    }
    

export default KitapKayitFormu;
